INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (500, '利物浦领袖当选FWA英超年度最佳 压曼城曼联双雄', '\n\n　　利物浦队长亨德森当选FWA（足球记者协会）评选的英超年度最佳球员。\n　　亨德森击败了包括队友范戴克和马内、曼城的德布劳内、曼联的拉什福德等竞争对手，以超过总票数1/4的得票当选。德布劳内和拉什福德的得票分列二三位。\n　　亨德森说：“我非常感谢投我票的人和足球记者协会，我感觉不能独自接受奖项，这个赛季我取得的任何成绩，都是团队努力的结果。”\n　　该奖项', 'https://sports.sina.com.cn/g/pl/2020-07-24/doc-iivhuipn4900330.shtml');
