INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (893, '三分7中5砍23分！广东00后小将刷两项生涯新高', '\n\n　　北京时间7月23日，CBA复赛第二阶段第17日，广东131-106大胜同曦，28连胜继续刷新CBA历史纪录。广东替补控卫00后小将徐杰发挥出色。\n　　本场比赛，徐杰替补登场，出战27分钟，16投9中，三分球7中5，拿到23分1篮板3助攻2抢断。\n\n\n　　徐杰的5记三分和23分均是生涯新高。\n　　（刘文明）\n\n\n\n\n\n', 'https://sports.sina.com.cn/basketball/cba/2020-07-23/doc-iivhuipn4743246.shtml');
