INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (903, '亚平宁之王!12年联赛夺冠起 尤文统治意甲3000天', '\n\n　　直播吧7月23日讯 据数据统计，自尤文图斯在2012年5月6日登顶意甲联赛开始，斑马军团对意甲联赛的统治已经长达3000天。\n　　目前，尤文图斯已经连续八次赢得意甲联赛冠军，这赛季他们正在全力冲击联赛九连冠。在已经进行34轮联赛，且少赛身后的亚特兰大一场的情况下，尤文图斯仍然拥有6分的优势。\n\n\n\n\n\n', 'https://sports.sina.com.cn/g/seriea/2020-07-23/doc-iivhuipn4737216.shtml');
