INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (1244, '“大吉大利”大同吃鸡！黄金联赛大同站落幕', '\n　　北京时间7月18日18时，2020新浪3×3黄金篮球联赛大同赛区的预赛拉开序幕。鲜红的落日为比赛挥旗震鼓，场外的观众也同赛场球员一同热血沸腾。\n　　A组：789俱乐部荣耀队 21 - 17 开拓者\n　　789俱乐部荣耀队 18- 15 啄木鸟\n　　789俱乐部荣耀队 20-16 秤砣\n　　B组：天生一队 6-19 CoCo\n　　CoCo队 6-21 大', 'https://sports.sina.com.cn/sportsevents/3v3/2020-07-30/doc-iivhvpwx8308794.shtml');
