INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (3579, '76人大危机！西帝左髌骨半脱位 暂无回归日程', '\n\n　　北京时间8月7日凌晨讯 据著名NBA记者沙姆斯（Shams）报道，76人当家球星之一本-西蒙斯遭遇左髌骨半脱位，MRI显示其韧带没有受伤，目前西蒙斯和球队正在考虑治疗方案，暂无回归时间表。\n　　在昨日对阵奇才的比赛进行至第三节还剩5分多钟时，西蒙斯持球面对特洛伊-布朗，他运球时左膝感到不适，随后便退出了比赛。\n　　（新体）\n\n\n\n\n\n', 'https://sports.sina.com.cn/basketball/nba/2020-08-07/doc-iivhuipn7262972.shtml');
