INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (4212, '心疼！阿联返回酒店脚缠冰袋步履缓慢', '\n\n\n　　直播吧8月8日讯 刚刚结束的一场比赛，广东男篮88比85力克北京男篮，以2比1的大比分晋级总决赛。\n　　赛后，广东队回到下榻的酒店，首先走出来的是球队主帅杜锋和总经理朱芳雨，二位曾经的战友，目前身份虽然有所改变，但共同经历了这样一场比赛之后，二人还是如释重负地相拥，满脸的喜悦溢于言表。\n\n\n\n\n\n', 'https://sports.sina.com.cn/basketball/cba/2020-08-08/doc-iivhuipn7596496.shtml');
