INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (4577, '杨鸣：三周前谁能想到我们能进决赛？', '\n\n　　直播吧8月7日讯 CBA半决赛，辽宁119-113击败新疆，成功杀入总决赛。赛后，辽宁主帅杨鸣更新了个人微博，感谢了球员们，并让球员们继续加油，为辽宁而战。\n　　杨鸣在微博中写道：“三周前谁能想到我们能进决赛？感谢这个团队的所有人特别是这帮小伙子们，充分诠释何为永不放弃！！！继续加油，为身后的图标《辽宁》而战。”\n\n\n\n\n\n', 'https://sports.sina.com.cn/basketball/cba/2020-08-07/doc-iivhvpwx9820876.shtml');
