INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (4580, '阿不都鼓励球队：下个赛季 我们一起从头再来', '\n\n　　直播吧8月7日讯 在刚刚结束的CBA季后赛半决赛中，新疆大比分0-2遭辽宁淘汰。\n　　新疆队一直在养伤的阿不都沙拉木更新社媒鼓励球队：“下个赛季，我们一起，从头再来。 ”\n\n\n\n\n\n', 'https://sports.sina.com.cn/basketball/cba/2020-08-07/doc-iivhvpwx9819670.shtml');
