INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (5139, 'Unicode', '\nUnicode（又称统一码、万国码、单一码）是计算机科学领域里的一项业界标准，包括字符集、编码方案等。Unicode是为了解决传统的字符编码方案的局限而产生的，它为每种语言中的每个字符设定了统一并且唯一的二进制编码，以满足跨语言、跨平台进行文本转换、处理的要求。1990年开始研发，1994年正式公布。\n', 'https://baike.baidu.com/item/Unicode');
