INSERT INTO `content`(`id`, `Title`, `content`, `link`) VALUES (5649, '莎拉波娃：疫情期间收获颇多 经常做饭+学习种植', '\n　　北京时间8月13日 前世界排名第一的俄罗斯球员莎拉波娃在接受采访时说道，在目前的隔离期内，她能和父母待在加州的家里是很幸运的一件事，这段时间她一直在家里尝试不同的活动，以及锻炼身体。\n\n　　莎拉波娃谈到她如何享受在家里度过的时光：“当我参加比赛的时候，我一年中只能在家里呆上几个星期，所以现在有更多的时间在家里休息放松，和家人们在一起真是太好了。”\n　　', 'https://sports.sina.com.cn/tennis/atp/2020-08-13/doc-iivhvpwy0814838.shtml');
